package com.programming.class_1;

public class loopconstruct {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=11;
		while(n<=11) {
			System.out.println(n + " ");
		n++;
		}
		System.out.println("hehehehehe");
		do {
			System.out.println(n + " ");
			n++;
		} while(n<=15);
		System.out.println("hehehehehe");
		for(int i=0;i<=n;i++)
		{
			System.out.println(i + " ");
		}
			

	}

}
